import React, {useState} from "react";
import ExpenseItems from "./ExpenseItems";
import ExpenseFilter from "./ExpenseFilter";
import Card from './UI/Card';
import './ExpenseList.css' ;

const ExpenseList = (props) => {

    const [ year, setYear ] = useState('2020')

    let expenses = [ ...props.expense ];

    const onChangeYear = (selectedYear) => {
        setYear(selectedYear)
        //console.log(year)

    }  

    const filteredExpenses = props.items.filter((expense => {
        return expense.date.getFullYear().toString() === year;
    }))

    return (
        <div>
            <Card className='expenses'>
                <div>
                <ExpenseFilter selectedYear={year} onChangeYear={onChangeYear}/>
                    {
                        expenses.map((expense, i) => {
                            return ( 
                                <ExpenseItems 
                                    amount={filteredExpenses[i].amount} 
                                    title={filteredExpenses[i].title} 
                                    date={filteredExpenses[i].date}
                                    key={filteredExpenses[i].id}
                                /> 
                            )
                        })
                    }
                </div>
            </Card>
        </div>
    );
};

export default ExpenseList;
